import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Events.css'; // Custom styles

function Events() {
  const [events, setEvents] = useState([
    { id: 1, title: "Tech Meetup", description: "A networking event for tech enthusiasts.", date: new Date() },
    { id: 2, title: "Hackathon 2025", description: "24-hour coding competition!", date: new Date() },
    { id: 3, title: "Annual Fest", description: "A celebration of culture and talent.", date: new Date() },
  ]);

  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());

  // Add Event Function
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription) {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        description: eventDescription,
        date: selectedDate,
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Delete Event Function
  const handleDeleteEvent = (id) => {
    setEvents(events.filter(event => event.id !== id));
  };

  // Handle Date Selection
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Filter Events by Selected Date
  const eventsForSelectedDate = events.filter(event =>
    new Date(event.date).toDateString() === new Date(selectedDate).toDateString()
  );

  return (
    <section className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-blue-900 text-white p-10 flex flex-col justify-between">
      <div className="max-w-6xl mx-auto flex-grow">
        <h2 className="text-4xl font-extrabold text-center mb-10 tracking-wider text-cyan-400 drop-shadow-lg">Event Calendar</h2>

        {/* Events for Selected Date */}
        <h3 className="text-3xl font-semibold text-center mb-4 text-cyan-400">
          Events on {selectedDate.toDateString()}
        </h3>
        {eventsForSelectedDate.length === 0 ? (
          <p className="text-center text-gray-400">No events scheduled for this date.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {eventsForSelectedDate.map((event) => (
              <div key={event.id} className="bg-black bg-opacity-50 border border-gray-700 backdrop-blur-md p-6 rounded-lg shadow-lg text-center transition duration-300 hover:scale-105 hover:shadow-cyan-500/50">
                <h3 className="text-2xl font-semibold mb-2 text-cyan-300">{event.title}</h3>
                <p className="text-gray-300">{event.description}</p>
                <p className="text-sm text-gray-400 mt-2">Date: {new Date(event.date).toDateString()}</p>
                <button 
                  onClick={() => handleDeleteEvent(event.id)}
                  className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300"
                >
                  Cancel Event
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Calendar & Add Event Form Section (At the Bottom) */}
      <div className="mt-10 flex flex-col md:flex-row items-center justify-center gap-10 bg-black bg-opacity-50 backdrop-blur-md p-6 rounded-lg shadow-lg max-w-5xl mx-auto border border-gray-700">
        {/* Calendar Section */}
        <div className="w-full md:w-1/2 text-center">
          <h3 className="text-2xl font-semibold mb-4 text-cyan-300">Select a Date</h3>
          <Calendar
            onChange={handleDateChange}
            value={selectedDate}
            className="mx-auto custom-calendar bg-gray-800 text-white"
          />
          <p className="mt-4 text-gray-300">
            Selected Date: <b>{selectedDate.toDateString()}</b>
          </p>
        </div>

        {/* Add Event Form */}
        <div className="w-full md:w-1/2">
          <h3 className="text-2xl font-semibold text-center mb-4 text-cyan-300">Add New Event</h3>
          <form onSubmit={handleAddEvent} className="flex flex-col items-center">
            <input 
              type="text" 
              placeholder="Event Title" 
              className="w-full px-4 py-2 mb-4 border border-gray-600 rounded-md focus:outline-none bg-gray-800 text-white placeholder-gray-400"
              value={eventTitle}
              onChange={(e) => setEventTitle(e.target.value)}
            />
            <textarea 
              placeholder="Event Description" 
              className="w-full px-4 py-2 mb-4 border border-gray-600 rounded-md focus:outline-none bg-gray-800 text-white placeholder-gray-400"
              rows={4}
              value={eventDescription}
              onChange={(e) => setEventDescription(e.target.value)}
            />
            <button 
              type="submit"
              className="bg-cyan-500 text-white px-6 py-2 rounded-md hover:bg-cyan-600 transition duration-300"
            >
              Add Event
            </button>
          </form>
        </div>
      </div>
    </section>
  );
}

export default Events;
