import React, { useState } from 'react';

function Resources() {
  const [resources, setResources] = useState([
    { id: 1, name: "Projector", isBooked: false },
    { id: 2, name: "Sound System", isBooked: true },
    { id: 3, name: "Conference Room", isBooked: false },
    { id: 4, name: "Microphone", isBooked: false },
    { id: 5, name: "Laptop", isBooked: true },
  ]);

  const [resourceName, setResourceName] = useState('');

  // Handle adding a new resource
  const handleAddResource = (e) => {
    e.preventDefault();
    if (resourceName) {
      const newResource = {
        id: Date.now(),
        name: resourceName,
        isBooked: false,
      };
      setResources([...resources, newResource]);
      setResourceName('');
    } else {
      alert("Please enter a resource name.");
    }
  };

  // Handle booking a resource
  const handleBookResource = (id) => {
    setResources(resources.map(resource =>
      resource.id === id ? { ...resource, isBooked: true } : resource
    ));
  };

  // Handle releasing a resource
  const handleReleaseResource = (id) => {
    setResources(resources.map(resource =>
      resource.id === id ? { ...resource, isBooked: false } : resource
    ));
  };

  // Handle deleting a resource
  const handleDeleteResource = (id) => {
    setResources(resources.filter(resource => resource.id !== id));
  };

  return (
    <section className="min-h-screen bg-gradient-to-r from-gray-900 via-black to-gray-900 text-white p-10">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-4xl font-bold text-center mb-10">Resource Management</h2>

        {/* Resource List */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {resources.map((resource) => (
            <div key={resource.id} className="bg-white bg-opacity-10 backdrop-blur-md p-6 rounded-lg shadow-lg text-center">
              <h3 className="text-2xl font-semibold mb-2">{resource.name}</h3>
              <p className={`text-lg font-semibold ${resource.isBooked ? "text-red-500" : "text-green-500"}`}>
                {resource.isBooked ? "Booked" : "Available"}
              </p>
              
              <div className="mt-4">
                {!resource.isBooked ? (
                  <button 
                    onClick={() => handleBookResource(resource.id)}
                    className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition duration-300 mr-2"
                  >
                    Book Resource
                  </button>
                ) : (
                  <button 
                    onClick={() => handleReleaseResource(resource.id)}
                    className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-600 transition duration-300 mr-2"
                  >
                    Release Resource
                  </button>
                )}
                <button 
                  onClick={() => handleDeleteResource(resource.id)}
                  className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Add Resource Form */}
        <div className="mt-10 bg-white bg-opacity-10 backdrop-blur-md p-6 rounded-lg shadow-lg max-w-lg mx-auto">
          <h3 className="text-2xl font-semibold text-center mb-4">Add New Resource</h3>
          <form onSubmit={handleAddResource} className="flex flex-col items-center">
            <input 
              type="text" 
              placeholder="Resource Name" 
              className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none text-black"
              value={resourceName}
              onChange={(e) => setResourceName(e.target.value)}
            />
            <button 
              type="submit"
              className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition duration-300"
            >
              Add Resource
            </button>
          </form>
        </div>
      </div>
    </section>
  );
}

export default Resources;
