import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaCalendarAlt, FaBook, FaInfoCircle, FaComments, FaChartBar, FaBullhorn, FaSignInAlt, FaSignOutAlt } from 'react-icons/fa';
import { useAuth } from '../contexts/AuthContext';
import { usePermissions } from '../hooks/usePermissions';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();
  const { user, logout } = useAuth();
  const permissions = usePermissions();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const navLinks = [
    { path: '/home', name: 'Home', icon: <FaHome /> },
    { path: '/events', name: 'Events', icon: <FaCalendarAlt /> },
    { path: '/resources', name: 'Resources', icon: <FaBook /> },
    { path: '/about', name: 'About', icon: <FaInfoCircle /> }
  ];

  // Add protected routes only for authorized users
  if (user && permissions.canManageCircular()) {
    navLinks.push(
      { path: '/forum', name: 'Forum', icon: <FaComments /> },
      { path: '/analytics', name: 'Analytics', icon: <FaChartBar /> },
      { path: '/circular', name: 'Circular', icon: <FaBullhorn /> }
    );
  }

  return (
    <nav className="bg-white shadow-sm">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link to="/" className="text-[28px] font-bold text-[#246BFD]">
            Eventify
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navLinks.map((link) => (
              <Link
                key={link.path}
                to={link.path}
                className={`${
                  isActive(link.path)
                    ? 'text-[#246BFD]'
                    : 'text-gray-600 hover:text-[#246BFD]'
                } flex items-center space-x-1 text-sm font-medium transition-colors duration-200`}
              >
                <span className="text-lg">{link.icon}</span>
                <span>{link.name}</span>
              </Link>
            ))}
            
            {user ? (
              <button
                onClick={logout}
                className="bg-[#246BFD] text-white px-6 py-2 rounded-lg text-sm font-medium hover:bg-blue-600 transition-colors duration-200 flex items-center space-x-2"
              >
                <FaSignOutAlt />
                <span>Logout</span>
              </button>
            ) : (
              <Link
                to="/auth"
                className="bg-[#246BFD] text-white px-6 py-2 rounded-lg text-sm font-medium hover:bg-blue-600 transition-colors duration-200 flex items-center space-x-2"
              >
                <FaSignInAlt />
                <span>Login</span>
              </Link>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-gray-500 hover:text-gray-600 focus:outline-none"
            >
              <svg
                className={`${isOpen ? 'hidden' : 'block'} h-6 w-6`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
              <svg
                className={`${isOpen ? 'block' : 'hidden'} h-6 w-6`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile menu */}
        <div className={`${isOpen ? 'block' : 'hidden'} md:hidden py-2`}>
          {navLinks.map((link) => (
            <Link
              key={link.path}
              to={link.path}
              className={`${
                isActive(link.path)
                  ? 'text-[#246BFD] bg-blue-50'
                  : 'text-gray-600 hover:text-[#246BFD] hover:bg-blue-50'
              } flex items-center space-x-2 px-4 py-2 text-sm font-medium transition-colors duration-200`}
              onClick={() => setIsOpen(false)}
            >
              <span className="text-lg">{link.icon}</span>
              <span>{link.name}</span>
            </Link>
          ))}
          
          {user ? (
            <button
              onClick={() => {
                logout();
                setIsOpen(false);
              }}
              className="w-full text-left px-4 py-2 text-sm font-medium text-gray-600 hover:text-[#246BFD] hover:bg-blue-50 flex items-center space-x-2"
            >
              <FaSignOutAlt />
              <span>Logout</span>
            </button>
          ) : (
            <Link
              to="/auth"
              className="block px-4 py-2 text-sm font-medium text-gray-600 hover:text-[#246BFD] hover:bg-blue-50 flex items-center space-x-2"
              onClick={() => setIsOpen(false)}
            >
              <FaSignInAlt />
              <span>Login</span>
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
