import React, { useState } from 'react';
import { Calendar } from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '..css'; // Create for additional styling

function Events() {
  // State for managing events
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [organizingClub, setOrganizingClub] = useState('');

  // Handle date change
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Add Event
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription && organizingClub) {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        description: eventDescription,
        club: organizingClub,
        date: selectedDate.toDateString(),
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
      setOrganizingClub('');
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Delete Event
  const handleDeleteEvent = (id) => {
    setEvents(events.filter((event) => event.id !== id));
  };

  return (
    <section className="py-10 px-10 bg-gray-100 min-h-screen">
      <h2 className="text-4xl font-bold text-primary text-center mb-10">Schedule an Event</h2>

      {/* Calendar */}
      <div className="flex flex-col items-center mb-8">
        <Calendar 
          onChange={handleDateChange} 
          value={selectedDate} 
          className="react-calendar shadow-lg" 
          view="month" 
          calendarType="ISO 8601"
        />
        <p className="mt-4 font-semibold text-lg">
          Selected Date: <span className="font-bold">{selectedDate.toDateString()}</span>
        </p>
      </div>

      {/* Add Event Form */}
      <form onSubmit={handleAddEvent} className="bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto">
        <h3 className="text-2xl font-semibold mb-4">Add New Event</h3>
        <input
          type="text"
          placeholder="Event Title"
          className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
          value={eventTitle}
          onChange={(e) => setEventTitle(e.target.value)}
        />
        <textarea
          placeholder="Event Description"
          className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
          rows={4}
          value={eventDescription}
          onChange={(e) => setEventDescription(e.target.value)}
        />
        <input
          type="text"
          placeholder="Organizing Club"
          className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
          value={organizingClub}
          onChange={(e) => setOrganizingClub(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-300"
        >
          Add Event
        </button>
      </form>

      {/* Event List */}
      <div className="mt-10">
        <h3 className="text-3xl font-semibold mb-4">Upcoming Events</h3>
        {events.length === 0 ? (
          <p className="text-gray-600">No events scheduled.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {events.map((event) => (
              <div key={event.id} className="bg-white p-6 rounded-lg shadow-lg">
                <h4 className="text-2xl font-semibold mb-2">{event.title}</h4>
                <p className="text-gray-600"><strong>Description:</strong> {event.description}</p>
                <p className="text-gray-600"><strong>Organizing Club:</strong> {event.club}</p>
                <p className="text-gray-500 text-sm"><strong>Date:</strong> {event.date}</p>
                <button
                  onClick={() => handleDeleteEvent(event.id)}
                  className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300"
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
}

export default Events;
