import React, { useState } from 'react';
import Calendar from 'react-calendar';
import { Link } from 'react-router-dom'; // Import Link for navigation
import 'react-calendar/dist/Calendar.css'; // Import calendar styles
import './Events.css'; // Optional: Custom CSS for styling

function Events() {
  const [events, setEvents] = useState([
    { 
      id: 1, 
      title: "Tech Meetup", 
      description: "Join us for a day of networking and innovation.", 
      date: new Date("2025-04-05"),
      club: "Tech Club",
      duration: "2 hours",
      venue: "Auditorium",
      additionalInfo: "Snacks will be provided."
    },
    { 
      id: 2, 
      title: "Hackathon 2025", 
      description: "Compete, collaborate, and create amazing projects.", 
      date: new Date("2025-04-15"),
      club: "Coding Club",
      duration: "24 hours",
      venue: "Lab 301",
      additionalInfo: "Bring your own laptop."
    }
  ]);

  // State for toggling the Add Event form
  const [showAddEvent, setShowAddEvent] = useState(false);

  // State for form inputs
  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [eventClub, setEventClub] = useState('');
  const [eventDuration, setEventDuration] = useState('');
  const [eventVenue, setEventVenue] = useState('');
  const [eventAdditionalInfo, setEventAdditionalInfo] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());

  // Add Event Function
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription && eventClub && eventDuration && eventVenue) {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        description: eventDescription,
        date: selectedDate,
        club: eventClub,
        duration: eventDuration,
        venue: eventVenue,
        additionalInfo: eventAdditionalInfo
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
      setEventClub('');
      setEventDuration('');
      setEventVenue('');
      setEventAdditionalInfo('');
      setShowAddEvent(false); // Hide form after adding event
    } else {
      alert("Please fill in all required fields.");
    }
  };

  // Delete Event Function
  const handleDeleteEvent = (id) => {
    const filteredEvents = events.filter(event => event.id !== id);
    setEvents(filteredEvents);
  };

  // Handle Date Selection
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Sort events into past and upcoming
  const currentDate = new Date();
  const pastEvents = events.filter(event => new Date(event.date) < currentDate);
  const upcomingEvents = events.filter(event => new Date(event.date) >= currentDate);

  return (
    <section className="py-20 px-10 bg-gray-100">
      <h2 className="text-4xl font-bold text-primary text-center mb-10">Event Management</h2>

      {/* Past and Upcoming Events */}
      <div className="mb-8 max-w-3xl mx-auto">
        <h3 className="text-2xl font-semibold text-gray-700 mb-4">Past Events</h3>
        {pastEvents.length === 0 ? (
          <p className="text-gray-600">No past events.</p>
        ) : (
          <ul className="list-disc pl-5 text-gray-700">
            {pastEvents.map(event => (
              <li key={event.id}>
                <Link to={`/events/${event.id}`} className="text-blue-500 hover:underline">
                  {event.title}
                </Link>
                <span className="text-gray-500 ml-2">({new Date(event.date).toDateString()})</span>
              </li>
            ))}
          </ul>
        )}

        <h3 className="text-2xl font-semibold text-gray-700 mt-6 mb-4">Upcoming Events</h3>
        {upcomingEvents.length === 0 ? (
          <p className="text-gray-600">No upcoming events.</p>
        ) : (
          <ul className="list-disc pl-5 text-gray-700">
            {upcomingEvents.map(event => (
              <li key={event.id}>
                <Link to={`/events/${event.id}`} className="text-blue-500 hover:underline">
                  {event.title}
                </Link>
                <span className="text-gray-500 ml-2">({new Date(event.date).toDateString()})</span>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Buttons for Add Event and Cancel Event */}
      <div className="flex justify-center gap-4 mb-8">
        <button 
          onClick={() => setShowAddEvent(!showAddEvent)}
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600 transition duration-300"
        >
          {showAddEvent ? "Close Form" : "Add Event"}
        </button>
      </div>

      {/* Add Event Form - Shown when button is clicked */}
      {showAddEvent && (
        <form onSubmit={handleAddEvent} className="mb-8 bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto">
          <h3 className="text-2xl font-semibold mb-4">Add New Event</h3>
          
          <input type="text" placeholder="Event Title" className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            value={eventTitle} onChange={(e) => setEventTitle(e.target.value)} />

          <textarea placeholder="Event Description" className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            rows={2} value={eventDescription} onChange={(e) => setEventDescription(e.target.value)} />

          <input type="text" placeholder="Organizing Club" className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            value={eventClub} onChange={(e) => setEventClub(e.target.value)} />

          <input type="text" placeholder="Event Duration (e.g. 2 hours)" className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            value={eventDuration} onChange={(e) => setEventDuration(e.target.value)} />

          <input type="text" placeholder="Venue" className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            value={eventVenue} onChange={(e) => setEventVenue(e.target.value)} />

          <textarea placeholder="Additional Information (optional)" className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            rows={2} value={eventAdditionalInfo} onChange={(e) => setEventAdditionalInfo(e.target.value)} />

          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-300">
            Add Event
          </button>
        </form>
      )}

      {/* Cancel Event Button for Upcoming Events */}
      <div className="flex flex-wrap gap-4 justify-center">
        {upcomingEvents.map(event => (
          <button key={event.id} onClick={() => handleDeleteEvent(event.id)}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300">
            Cancel {event.title}
          </button>
        ))}
      </div>
    </section>
  );
}

export default Events;
