export const ROLES = {
  CLUB_HEAD: 'CLUB_HEAD',
  DIRECTOR_STUDENT_AFFAIRS: 'DIRECTOR_STUDENT_AFFAIRS',
  DIRECTOR_IQAC: 'DIRECTOR_IQAC',
  CDC: 'CDC',
  AEC: 'AEC',
  STUDENT: 'STUDENT',
  VISITOR: 'VISITOR'
};

export const AUTHORIZED_ROLES = [
  ROLES.CLUB_HEAD,
  ROLES.DIRECTOR_STUDENT_AFFAIRS,
  ROLES.DIRECTOR_IQAC,
  ROLES.CDC,
  ROLES.AEC
];

export const PREDEFINED_CREDENTIALS = {
  'clubhead@cbit.ac.in': {
    password: 'clubhead123',
    role: ROLES.CLUB_HEAD
  },
  'dsa@cbit.ac.in': {
    password: 'dsa123',
    role: ROLES.DIRECTOR_STUDENT_AFFAIRS
  },
  'iqac@cbit.ac.in': {
    password: 'iqac123',
    role: ROLES.DIRECTOR_IQAC
  },
  'cdc@cbit.ac.in': {
    password: 'cdc123',
    role: ROLES.CDC
  },
  'aec@cbit.ac.in': {
    password: 'aec123',
    role: ROLES.AEC
  }
};

export const hasPermission = (userRole, requiredRole) => {
  if (!userRole) return false;
  return AUTHORIZED_ROLES.includes(userRole);
};
