import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Calendar styles
import './Events.css'; // Custom styles for dark mode

function Events() {
  const [events, setEvents] = useState([
    { id: 1, title: "Tech Meetup", description: "Join us for networking and innovation.", date: new Date() },
    { id: 2, title: "Hackathon 2025", description: "Compete and create amazing projects.", date: new Date() },
    { id: 3, title: "Annual Fest", description: "A celebration of talent and creativity.", date: new Date() },
  ]);

  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());

  // Add Event Function
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription) {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        description: eventDescription,
        date: selectedDate,
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Delete Event Function
  const handleDeleteEvent = (id) => {
    setEvents(events.filter(event => event.id !== id));
  };

  // Handle Date Selection
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Filter Events by Selected Date
  const eventsForSelectedDate = events.filter(event =>
    new Date(event.date).toDateString() === new Date(selectedDate).toDateString()
  );

  return (
    <section className="min-h-screen bg-gradient-to-r from-gray-900 via-black to-gray-900 text-white p-10">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-4xl font-bold text-center mb-10">Event Calendar</h2>

        {/* Calendar Section */}
        <div className="mb-8 max-w-md mx-auto bg-white bg-opacity-10 backdrop-blur-md p-6 rounded-lg shadow-lg">
          <h3 className="text-2xl font-semibold mb-4 text-center">Schedule an Event</h3>
          <Calendar
            onChange={handleDateChange}
            value={selectedDate}
            className="mx-auto custom-calendar"
          />
          <p className="mt-4 text-center text-gray-300">
            Selected Date: <b>{selectedDate.toDateString()}</b>
          </p>
        </div>

        {/* Add Event Form (At the Bottom) */}
        <div className="mt-10 bg-white bg-opacity-10 backdrop-blur-md p-6 rounded-lg shadow-lg max-w-lg mx-auto">
          <h3 className="text-2xl font-semibold text-center mb-4">Add New Event</h3>
          <form onSubmit={handleAddEvent} className="flex flex-col items-center">
            <input 
              type="text" 
              placeholder="Event Title" 
              className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none text-black"
              value={eventTitle}
              onChange={(e) => setEventTitle(e.target.value)}
            />
            <textarea 
              placeholder="Event Description" 
              className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none text-black"
              rows={4}
              value={eventDescription}
              onChange={(e) => setEventDescription(e.target.value)}
            />
            <button 
              type="submit"
              className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition duration-300"
            >
              Add Event
            </button>
          </form>
        </div>

        {/* Events for Selected Date */}
        <h3 className="text-3xl font-semibold text-center mt-12 mb-4">
          Events on {selectedDate.toDateString()}
        </h3>
        {eventsForSelectedDate.length === 0 ? (
          <p className="text-center text-gray-400">No events scheduled for this date.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {eventsForSelectedDate.map((event) => (
              <div key={event.id} className="bg-white bg-opacity-10 backdrop-blur-md p-6 rounded-lg shadow-lg text-center">
                <h3 className="text-2xl font-semibold mb-2">{event.title}</h3>
                <p className="text-gray-300">{event.description}</p>
                <p className="text-sm text-gray-400 mt-2">Date: {new Date(event.date).toDateString()}</p>
                <button 
                  onClick={() => handleDeleteEvent(event.id)}
                  className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300"
                >
                  Cancel Event
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
}

export default Events;
