import React from 'react';

// Data for College Clubs (Enhanced with History & Achievements)
const clubCategories = [
  {
    category: "Cultural Clubs",
    clubs: [
      {
        name: "Chaitanya Samskruthi",
        founded: "2010",
        mission: "To promote cultural awareness and activities among students.",
        achievements: [
          "Organized annual cultural fest with over 500 participants.",
          "Won 'Best Cultural Club' award in 2023."
        ],
        instagram: "https://www.instagram.com/chaitanya_samskruthi"
      },
      {
        name: "Chaitanya Geethi",
        founded: "2012",
        mission: "Encouraging students in music and performing arts.",
        achievements: [
          "Hosted 'Sangeet Utsav' with 1,000+ attendees.",
          "Performed at national-level cultural events."
        ],
        instagram: "https://www.instagram.com/chaitanya_geethi"
      },
      {
        name: "CBIT Photography Club",
        founded: "2015",
        mission: "Capturing memories and moments through the lens.",
        achievements: [
          "Won inter-college photography competition twice.",
          "Official photography partner for CBIT Fest."
        ],
        instagram: "https://www.instagram.com/cbitphotoclub"
      }
    ],
  },
  {
    category: "Technical Clubs",
    clubs: [
      {
        name: "CBIT Developer Student Club",
        founded: "2018",
        mission: "Empowering students with development and coding skills.",
        achievements: [
          "Conducted 50+ workshops on web and app development.",
          "Represented CBIT in Google Developer Fest."
        ],
        instagram: "https://www.instagram.com/cbitdsc"
      },
      {
        name: "IEEE CBIT",
        founded: "2005",
        mission: "Promoting research and innovation in technology.",
        achievements: [
          "Published 30+ research papers.",
          "Won the 'Best IEEE Student Branch' award in 2022."
        ],
        instagram: "https://www.instagram.com/ieee_cbit"
      }
    ],
  },
  {
    category: "Sports Clubs",
    clubs: [
      {
        name: "Chaitanya Kreeda",
        founded: "2010",
        mission: "Encouraging sportsmanship and fitness among students.",
        achievements: [
          "Won 5+ university-level sports tournaments.",
          "Trained state-level athletes in football and basketball."
        ],
        instagram: "https://www.instagram.com/chaitanyakreeda"
      }
    ],
  }
];

// Function to generate image path dynamically
const getImagePath = (clubName) => {
  return `/assets/images/clubs/${clubName.toLowerCase().replace(/ /g, "_")}.jpg`;
};

function About() {
  return (
    <section className="py-20 px-10 bg-white text-center">
      <h2 className="text-4xl font-bold text-primary mb-4">About Eventify</h2>
      <p className="text-gray-600 text-lg mb-8">
        Eventify is a club management platform designed to streamline event organization, resource booking, and communication. 
        It simplifies managing college events by providing an intuitive interface for organizers and attendees alike.
      </p>

      <h2 className="text-4xl font-bold text-primary mb-8">Our College Clubs</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
        {clubCategories.map((category, index) => (
          <div key={index} className="bg-gray-100 shadow-lg rounded-lg p-6 hover:shadow-2xl transition-all duration-300">
            <h3 className="text-2xl font-semibold text-primary mb-4">{category.category}</h3>

            {category.clubs.map((club, idx) => (
              <div key={idx} className="mb-6 text-left">
                <img 
                  src={getImagePath(club.name)} 
                  alt={club.name} 
                  className="w-full h-48 object-cover rounded-lg mb-4"
                  onError={(e) => e.target.src = "/assets/images/default-club.jpg"} // Fallback image if not found
                />
                <h4 className="text-xl font-semibold text-gray-800">{club.name}</h4>
                <p className="text-gray-600"><strong>Founded:</strong> {club.founded}</p>
                <p className="text-gray-600"><strong>Mission:</strong> {club.mission}</p>
                <p className="text-gray-600"><strong>Achievements:</strong></p>
                <ul className="list-disc list-inside text-gray-700 ml-4">
                  {club.achievements.map((achievement, i) => (
                    <li key={i}>{achievement}</li>
                  ))}
                </ul>
                <a 
                  href={club.instagram} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="text-blue-500 hover:underline mt-2 inline-block"
                >
                  Visit Instagram
                </a>
              </div>
            ))}
          </div>
        ))}
      </div>
    </section>
  );
}

export default About;
