import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Import calendar styles
import './Events.css'; // Optional: Custom CSS for styling (create if needed)

function Events() {
  // State to hold list of events
  const [events, setEvents] = useState([
    { id: 1, title: "Tech Meetup", description: "Join us for a day of networking and innovation.", date: new Date() },
    { id: 2, title: "Hackathon 2025", description: "Compete, collaborate, and create amazing projects.", date: new Date() },
    { id: 3, title: "Annual Fest", description: "Celebrate the best of talent and creativity.", date: new Date() },
  ]);

  // State for form input
  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());

  // Add Event Function
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription) {
      const newEvent = {
        id: Date.now(), // Unique ID based on timestamp
        title: eventTitle,
        description: eventDescription,
        date: selectedDate,
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Delete Event Function
  const handleDeleteEvent = (id) => {
    const filteredEvents = events.filter(event => event.id !== id);
    setEvents(filteredEvents);
  };

  // Handle Date Selection
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Filter Events by Selected Date
  const eventsForSelectedDate = events.filter(event => 
    new Date(event.date).toDateString() === new Date(selectedDate).toDateString()
  );

  return (
    <section className="py-20 px-10 bg-gray-100">
      <h2 className="text-4xl font-bold text-primary text-center mb-10">Upcoming Events</h2>

      {/* Calendar Section */}
      <div className="mb-8 max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-2xl font-semibold mb-4">Schedule an Event</h3>
        <Calendar 
          onChange={handleDateChange} 
          value={selectedDate} 
          className="mx-auto"
        />
        <p className="mt-4 text--600 text-center">
          Selected Date: <b>{selectedDate.toDateString()}</b>
        </p>
      </div>

      {/* Add Event Form */}
      <form onSubmit={handleAddEvent} className="mb-8 bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto">
        <h3 className="text-2xl font-semibold mb-4">Add New Event</h3>
        <input 
          type="text" 
          placeholder="Event Title" 
          className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
          value={eventTitle}
          onChange={(e) => setEventTitle(e.target.value)}
        />
        <textarea 
          placeholder="Event Description" 
          className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
          rows={4}
          value={eventDescription}
          onChange={(e) => setEventDescription(e.target.value)}
        />
        <button 
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-300"
        >
          Add Event
        </button>
      </form>

      {/* Events for Selected Date */}
      <h3 className="text-3xl font-semibold text-primary text-center mb-4">
        Events on {selectedDate.toDateString()}
      </h3>
      {eventsForSelectedDate.length === 0 ? (
        <p className="text-center text-gray-600">No events scheduled for this date.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {eventsForSelectedDate.map((event) => (
            <div key={event.id} className="bg-white p-6 rounded-lg shadow-lg">
              <h3 className="text-2xl font-semibold mb-2">{event.title}</h3>
              <p className="text-gray-600">{event.description}</p>
              <p className="text-sm text-gray-400 mt-2">Date: {new Date(event.date).toDateString()}</p>
              <button 
                onClick={() => handleDeleteEvent(event.id)}
                className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-300"
              >
                Cancel Event
              </button>
            </div>
          ))}
        </div>
      )}
    </section>
  );
}

export default Events;
