import React from 'react';

// Data for College Clubs (Categorized with Instagram Links)
const clubCategories = [
  {
    category: "Cultural Clubs",
    image: "/assets/images/csclub.jpg",
    clubs: [
      { name: "Chaitanya Samskruthi", instagram: "https://www.instagram.com/chaitanya_samskruthi" },
      { name: "Chaitanya Geethi", instagram: "https://www.instagram.com/chaitanya_geethi" },
      { name: "Chaitanya Vaadya", instagram: "https://www.instagram.com/chaitanya_vaadya" },
      { name: "UDC", instagram: "https://www.instagram.com/uniteddancecrew" },
      { name: "Chaitanya Chaaya", instagram: "https://www.instagram.com/chaaya.cbit" },
      { name: "Chaitanya Srujana", instagram: "https://www.instagram.com/chaitanya_srujana_cbit" },
    ],
  },
  {
    category: "Technical Clubs",
    image: "/assets/images/tech.jpg",
    clubs: [
      { name: "Chaitanya Astra", instagram: "https://www.instagram.com/chaitanyaastra" },
      { name: "Praheti Racing", instagram: "https://www.instagram.com/prahetiracing" },
      { name: "CBIT Developer Student Club", instagram: "https://www.instagram.com/cbitdsc" },
    ],
  },
  {
    category: "Chaitanya Seva Clubs",
    image: "/assets/images/seva.jpg",
    clubs: [
      { name: "NSS", instagram: "https://www.instagram.com/cbitnss" },
      { name: "ELC", instagram: "https://www.instagram.com/elc.cbit" },
      { name: "Chaitanya Spandana", instagram: "https://www.instagram.com/chaitanya.spandana" },
    ],
  },
  {
    category: "Sports Clubs",
    image: "/assets/images/kreeda.jpg",
    clubs: [
      { name: "Chaitanya Kreeda", instagram: "https://www.instagram.com/chaitanyakreeda" },
      { name: "E-Sports", instagram: "https://www.instagram.com/cbitesports" },
    ],
  },
];

function About() {
  return (
    <section className="py-20 px-10 bg-white text-center">
      <h2 className="text-4xl font-bold text-primary mb-4">About Eventify</h2>
      <p className="text-gray-600 text-lg">
        Eventify is a club management platform designed to streamline event organization, resource booking, and communication. 
        It simplifies managing college events by providing an intuitive interface for organizers and attendees alike.
      </p>

      <br />
      <h2 className="text-4xl font-bold text-primary mb-8">Our College Clubs</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        {clubCategories.map((category, index) => (
          <div 
            key={index} 
            className="bg-gray-100 shadow-lg rounded-lg p-6 hover:shadow-2xl transition-all duration-300"
          >
            <img 
              src={category.image} 
              alt={`${category.category}`} 
              className="w-full h-48 object-cover rounded-lg mb-4"
            />
            <h3 className="text-2xl font-semibold text-primary mb-4">{category.category}</h3>

            <ul className="text-left list-disc list-inside">
              {category.clubs.map((club, idx) => (
                <li key={idx} className="mb-2 text-gray-700">
                  <a 
                    href={club.instagram} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-500 hover:underline"
                  >
                    {club.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}

export default About;
