import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Events.css'; // Optional for custom styles

function Events() {
  // State for calendar date
  const [selectedDate, setSelectedDate] = useState(new Date());
  
  // State for events
  const [events, setEvents] = useState([]);

  // State for event title
  const [eventTitle, setEventTitle] = useState('');

  // Handle date change
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Add event
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle.trim() !== '') {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        date: selectedDate.toDateString(),
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
    } else {
      alert("Please enter an event title.");
    }
  };

  // Delete event
  const handleDeleteEvent = (id) => {
    const filteredEvents = events.filter(event => event.id !== id);
    setEvents(filteredEvents);
  };

  return (
    <section className="py-20 px-10 bg-gray-100 min-h-screen">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-3xl mx-auto">
        <h2 className="text-4xl font-bold text-center mb-6">Schedule an Event</h2>

        {/* Calendar Component */}
        <Calendar 
          onChange={handleDateChange} 
          value={selectedDate} 
          className="mb-6"
        />
        
        <p className="text-center text-lg font-semibold">
          Selected Date: <span className="font-bold">{selectedDate.toDateString()}</span>
        </p>

        {/* Event Form */}
        <form onSubmit={handleAddEvent} className="mt-6">
          <input 
            type="text" 
            placeholder="Event Title" 
            className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none"
            value={eventTitle}
            onChange={(e) => setEventTitle(e.target.value)}
          />
          <button 
            type="submit"
            className="w-full bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-300"
          >
            Add Event
          </button>
        </form>

        {/* Event List */}
        {events.length > 0 && (
          <div className="mt-8">
            <h3 className="text-2xl font-semibold mb-4">Scheduled Events</h3>
            <ul className="space-y-4">
              {events
                .filter(event => event.date === selectedDate.toDateString())
                .map(event => (
                  <li 
                    key={event.id} 
                    className="bg-gray-100 p-4 rounded-md shadow-md flex justify-between items-center"
                  >
                    <span>{event.title}</span>
                    <button 
                      onClick={() => handleDeleteEvent(event.id)}
                      className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-600"
                    >
                      Cancel
                    </button>
                  </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </section>
  );
}

export default Events;
