import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Import calendar styles
import { FaCalendarAlt, FaClock, FaMapMarkerAlt, FaTag, FaTrash } from 'react-icons/fa';

function Events() {
  // State to hold list of events
  const [events, setEvents] = useState([
    { id: 1, title: "Tech Meetup", description: "Join us for networking and innovation.", date: new Date(), time: "10:00 AM", location: "Auditorium", category: "Technology" },
    { id: 2, title: "Hackathon 2025", description: "Compete, collaborate, and create amazing projects.", date: new Date(), time: "9:00 AM", location: "Lab A", category: "Coding" },
    { id: 3, title: "Annual Fest", description: "Celebrate talent and creativity.", date: new Date(), time: "6:00 PM", location: "Main Hall", category: "Cultural" },
  ]);

  // Form input states
  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [eventTime, setEventTime] = useState('');
  const [eventLocation, setEventLocation] = useState('');
  const [eventCategory, setEventCategory] = useState('');
  const [submitted, setSubmitted] = useState(false);

  // Add Event Function
  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription && eventTime && eventLocation && eventCategory) {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        description: eventDescription,
        date: selectedDate,
        time: eventTime,
        location: eventLocation,
        category: eventCategory,
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
      setEventTime('');
      setEventLocation('');
      setEventCategory('');
      setSubmitted(true);
      setTimeout(() => setSubmitted(false), 3000);
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Delete Event Function
  const handleDeleteEvent = (id) => {
    setEvents(events.filter(event => event.id !== id));
  };

  // Handle Date Selection
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Filter Events by Selected Date
  const eventsForSelectedDate = events.filter(event => 
    new Date(event.date).toDateString() === new Date(selectedDate).toDateString()
  );

  return (
    <section className="py-20 px-10 bg-gray-100 min-h-screen">
      <h2 className="text-4xl font-bold text-blue-600 text-center mb-10">ðŸ“… Upcoming Events</h2>

      {/* Calendar Section */}
      <div className="mb-8 max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-2xl font-semibold mb-4 text-center">Pick a Date</h3>
        <Calendar
          onChange={handleDateChange}
          value={selectedDate}
          className="mx-auto custom-calendar"
        />
        <p className="mt-4 text-gray-600 text-center">
          Selected Date: <b>{selectedDate.toDateString()}</b>
        </p>
      </div>

      {/* Add Event Form */}
      <form onSubmit={handleAddEvent} className="mb-8 bg-white p-6 rounded-lg shadow-lg max-w-lg mx-auto">
        <h3 className="text-2xl font-semibold mb-4 text-center">ðŸŽ‰ Add New Event</h3>
        
        <div className="space-y-4">
          <div>
            <label className="block font-semibold text-gray-700">Event Title</label>
            <input 
              type="text" 
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-400 transition"
              placeholder="Enter event title"
              value={eventTitle}
              onChange={(e) => setEventTitle(e.target.value)}
              required
            />
          </div>

          <div>
            <label className="block font-semibold text-gray-700">Description</label>
            <textarea 
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-400 transition"
              rows={3}
              placeholder="Provide a short description"
              value={eventDescription}
              onChange={(e) => setEventDescription(e.target.value)}
              required
            ></textarea>
          </div>

          <div>
            <label className="block font-semibold text-gray-700">Time</label>
            <input 
              type="time" 
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-400 transition"
              value={eventTime}
              onChange={(e) => setEventTime(e.target.value)}
              required
            />
          </div>

          <div>
            <label className="block font-semibold text-gray-700">Location</label>
            <input 
              type="text" 
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-400 transition"
              placeholder="Enter event location"
              value={eventLocation}
              onChange={(e) => setEventLocation(e.target.value)}
              required
            />
          </div>

          <div>
            <label className="block font-semibold text-gray-700">Category</label>
            <select 
              className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-400 transition"
              value={eventCategory}
              onChange={(e) => setEventCategory(e.target.value)}
              required
            >
              <option value="">Select a category</option>
              <option value="Technology">Technology</option>
              <option value="Sports">Sports</option>
              <option value="Cultural">Cultural</option>
              <option value="Networking">Networking</option>
            </select>
          </div>

          <button 
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition"
          >
            Add Event
          </button>
        </div>

        {submitted && (
          <p className="mt-4 text-center text-green-600 font-semibold">
            ðŸŽ‰ Event added successfully!
          </p>
        )}
      </form>

      {/* Events for Selected Date */}
      <h3 className="text-3xl font-semibold text-blue-600 text-center mb-4">
        ðŸ“† Events on {selectedDate.toDateString()}
      </h3>
      
      {eventsForSelectedDate.length === 0 ? (
        <p className="text-center text-gray-600">No events scheduled for this date.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {eventsForSelectedDate.map((event) => (
            <div key={event.id} className="bg-white p-6 rounded-lg shadow-lg">
              <h3 className="text-2xl font-semibold">{event.title}</h3>
              <p className="text-gray-600">{event.description}</p>
              <p className="mt-2 text-gray-400 flex items-center"><FaClock className="mr-2" /> {event.time}</p>
              <p className="mt-2 text-gray-400 flex items-center"><FaMapMarkerAlt className="mr-2" /> {event.location}</p>
              <p className="mt-2 text-gray-400 flex items-center"><FaTag className="mr-2" /> {event.category}</p>
              <button 
                onClick={() => handleDeleteEvent(event.id)}
                className="mt-4 bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition flex items-center"
              >
                <FaTrash className="mr-2" /> Cancel Event
              </button>
            </div>
          ))}
        </div>
      )}
    </section>
  );
}

export default Events;
