import React from 'react';

// Sample Data for Clubs and Events (Replace with dynamic data later)
const clubs = [
  {
    name: "Tech Club",
    description: "A club dedicated to fostering technological innovation and creativity.",
    events: [
      { title: "Hackathon 2025", date: "April 15, 2025", description: "48-hour coding marathon." },
      { title: "AI Workshop", date: "May 5, 2025", description: "Introduction to Machine Learning." }
    ]
  },
  {
    name: "Cultural Club",
    description: "Promoting cultural diversity and organizing festive events.",
    events: [
      { title: "Annual Fest", date: "March 20, 2025", description: "Music, dance, and cultural performances." },
      { title: "Food Fiesta", date: "June 10, 2025", description: "Celebrating cuisines from around the world." }
    ]
  },
  {
    name: "Sports Club",
    description: "Encouraging physical fitness and competitive sports.",
    events: [
      { title: "Football Tournament", date: "July 1, 2025", description: "Inter-college football competition." },
      { title: "Marathon 2025", date: "August 15, 2025", description: "Annual marathon to promote fitness." }
    ]
  }
];

function About() {
  return (
    <section className="py-20 px-10 bg-white text-center">
      {/* About Eventify */}
      <h2 className="text-4xl font-bold text-primary mb-4">About Eventify</h2>
      <p className="text-gray-600 text-lg">
        Eventify is a club management platform designed to streamline event organization,
        resource booking, and communication. Managing college events can be challenging â€” 
        from coordinating venues and resources to tracking attendance and engagement.
        Eventify simplifies this process by offering an intuitive interface.
      </p>

      <br />
      <h2 className="text-4xl font-bold text-primary mb-4">Our Mission</h2>
      <p className="text-gray-600 text-lg">
        Our goal is to reduce the manual effort in managing events and foster greater student 
        engagement by providing an interactive, secure, and scalable platform. Whether you're 
        organizing a fundraiser, a workshop, or a cultural event, Eventify ensures a seamless 
        experience for organizers and attendees alike.
      </p>

      <br />
      <br />

      {/* College Clubs and Events */}
      <h2 className="text-4xl font-bold text-primary mb-8">Our College Clubs & Events</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {clubs.map((club, index) => (
          <div 
            key={index} 
            className="bg-gray-100 shadow-lg rounded-lg p-6 hover:shadow-2xl transition-all duration-300"
          >
            <h3 className="text-2xl font-semibold text-primary mb-2">{club.name}</h3>
            <p className="text-gray-700 mb-4">{club.description}</p>
            
            <h4 className="text-xl font-semibold text-secondary mb-2">Upcoming Events:</h4>
            <ul className="text-left list-disc list-inside">
              {club.events.map((event, idx) => (
                <li key={idx} className="mb-2">
                  <span className="font-medium">{event.title}</span> - <span className="text-gray-600">{event.date}</span>
                  <p className="text-sm text-gray-500">{event.description}</p>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}

export default About;
