import React, { useState } from 'react';
import Calendar from 'react-calendar';
import { Link } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import './Events.css';

function Events() {
  const [events, setEvents] = useState([
    { id: 1, title: "Tech Meetup", description: "Networking and innovation.", date: new Date("2025-04-05"), club: "Tech Club", venue: "Auditorium" },
    { id: 2, title: "Hackathon 2025", description: "Compete and create projects.", date: new Date("2025-04-15"), club: "Coding Club", venue: "Lab 301" }
  ]);

  const [showAddEvent, setShowAddEvent] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [eventToCancel, setEventToCancel] = useState(null);
  const [cancelReason, setCancelReason] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [eventTitle, setEventTitle] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [eventClub, setEventClub] = useState('');
  const [eventVenue, setEventVenue] = useState('');

  const handleAddEvent = (e) => {
    e.preventDefault();
    if (eventTitle && eventDescription && eventClub && eventVenue) {
      const newEvent = {
        id: Date.now(),
        title: eventTitle,
        description: eventDescription,
        date: selectedDate,
        club: eventClub,
        venue: eventVenue
      };
      setEvents([...events, newEvent]);
      setEventTitle('');
      setEventDescription('');
      setEventClub('');
      setEventVenue('');
      setShowAddEvent(false);
    } else {
      alert("Please fill in all required fields.");
    }
  };

  const handleCancelEvent = () => {
    if (eventToCancel && cancelReason) {
      setEvents(events.filter(event => event.id !== eventToCancel));
      setShowCancelModal(false);
      setCancelReason('');
      setEventToCancel(null);
    } else {
      alert("Please select a reason for cancellation.");
    }
  };

  return (
    <section className="py-20 px-10 bg-gray-100">
      <h2 className="text-4xl font-bold text-primary text-center mb-10">Event Management</h2>

      {/* Past and Upcoming Events */}
      <div className="mb-8 max-w-3xl mx-auto">
        <h3 className="text-2xl font-semibold text-gray-700 mb-4">Upcoming Events</h3>
        {events.length === 0 ? (
          <p className="text-gray-600">No upcoming events.</p>
        ) : (
          <ul className="list-disc pl-5 text-gray-700">
            {events.map(event => (
              <li key={event.id} className="mb-2">
                <Link to={`/events/${event.id}`} className="text-blue-500 hover:underline">{event.title}</Link>
                <span className="text-gray-500 ml-2">({new Date(event.date).toDateString()})</span>
                <button 
                  onClick={() => { setEventToCancel(event.id); setShowCancelModal(true); }} 
                  className="ml-4 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Cancel
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Add Event Button */}
      <div className="flex justify-center mb-8">
        <button 
          onClick={() => setShowAddEvent(!showAddEvent)}
          className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600"
        >
          {showAddEvent ? "Close Form" : "Add Event"}
        </button>
      </div>

      {/* Add Event Form with Calendar */}
      {showAddEvent && (
        <form onSubmit={handleAddEvent} className="mb-8 bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto">
          <h3 className="text-2xl font-semibold mb-4">Add New Event</h3>

          <label className="block text-gray-700">Event Title</label>
          <input type="text" className="w-full px-4 py-2 mb-4 border rounded-md" value={eventTitle} onChange={(e) => setEventTitle(e.target.value)} />

          <label className="block text-gray-700">Event Description</label>
          <textarea className="w-full px-4 py-2 mb-4 border rounded-md" rows={2} value={eventDescription} onChange={(e) => setEventDescription(e.target.value)} />

          <label className="block text-gray-700">Organizing Club</label>
          <input type="text" className="w-full px-4 py-2 mb-4 border rounded-md" value={eventClub} onChange={(e) => setEventClub(e.target.value)} />

          <label className="block text-gray-700">Venue</label>
          <input type="text" className="w-full px-4 py-2 mb-4 border rounded-md" value={eventVenue} onChange={(e) => setEventVenue(e.target.value)} />

          {/* Calendar Inside the Form */}
          <div className="mb-4">
            <h3 className="text-gray-700 mb-2">Select Date</h3>
            <Calendar onChange={setSelectedDate} value={selectedDate} className="mx-auto" />
            <p className="mt-2 text-gray-600">Selected Date: <b>{selectedDate.toDateString()}</b></p>
          </div>

          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">
            Add Event
          </button>
        </form>
      )}

      {/* Cancel Event Modal */}
      {showCancelModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-2xl font-semibold mb-4">Cancel Event</h3>
            <p className="mb-2">Why do you want to cancel?</p>

            <div className="mb-4">
              <label className="flex items-center mb-2">
                <input type="radio" name="cancelReason" value="Personal reasons" onChange={(e) => setCancelReason(e.target.value)} />
                <span className="ml-2">Personal reasons</span>
              </label>
              <label className="flex items-center mb-2">
                <input type="radio" name="cancelReason" value="Scheduling conflict" onChange={(e) => setCancelReason(e.target.value)} />
                <span className="ml-2">Scheduling conflict</span>
              </label>
              <label className="flex items-center mb-2">
                <input type="radio" name="cancelReason" value="Low participation" onChange={(e) => setCancelReason(e.target.value)} />
                <span className="ml-2">Low participation</span>
              </label>
            </div>

            <div className="flex justify-between">
              <button onClick={() => setShowCancelModal(false)} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">
                Close
              </button>
              <button onClick={handleCancelEvent} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">
                Confirm Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
}

export default Events;
