import React from 'react';

const clubCategories = [
  {
    category: "Cultural Clubs",
    image: "/assets/images/csclub.jpg",
    clubs: [
      { name: "Chaitanya Samskruthi", instagram: "https://www.instagram.com/chaitanya_samskruthi" },
      { name: "Chaitanya Geethi", instagram: "https://www.instagram.com/chaitanya_geethi" },
      { name: "Chaitanya Vaadya", instagram: "https://www.instagram.com/chaitanya_vaadya" },
      { name: "UDC", instagram: "https://www.instagram.com/uniteddancecrew" },
      { name: "Chaitanya Chaaya", instagram: "https://www.instagram.com/chaaya.cbit" },
      { name: "Chaitanya Srujana", instagram: "https://www.instagram.com/chaitanya_srujana_cbit" },
      { name: "Communicando", instagram: "https://www.instagram.com/communicando" },
      { name: "Toastmasters", instagram: "https://www.instagram.com/tmcbit" },
      { name: "CBIT MUN", instagram: "https://www.instagram.com/cbitmunhyd" },
      { name: "Transcendent", instagram: "https://www.instagram.com/transcendent.cbit" },
      { name: "Writers and Poets Club", instagram: "https://www.instagram.com/wpc_cbit" },
      { name: "CBIT Photography Club", instagram: "https://www.instagram.com/cbitphotoclub" },
    ],
  },
  {
    category: "Technical Clubs",
    image: "/assets/images/tech.jpg",
    clubs: [
      { name: "Chaitanya Astra", instagram: "https://www.instagram.com/chaitanyaastra" },
      { name: "Praheti Racing", instagram: "https://www.instagram.com/prahetiracing" },
      { name: "CBIT Developer Student Club", instagram: "https://www.instagram.com/cbitdsc" },
      { name: "HiCon", instagram: "https://www.instagram.com/hicon_cbit" },
      { name: "COSC", instagram: "https://www.instagram.com/cbitosc" },
      { name: "CCC", instagram: "https://www.instagram.com/ccc_cbit" },
      { name: "Neural Nexus CBIT", instagram: "https://www.instagram.com/neuralnexuscbit" },
      { name: "Ham Radio Club", instagram: "https://www.instagram.com/hrc.cbit" },
    ],
  },
];

function About() {
  return (
    <section className="py-16 px-8 bg-gradient-to-br from-indigo-50 via-purple-100 to-indigo-200 text-center">
      <h2 className="text-4xl font-bold text-indigo-900 mb-6">About Eventify</h2>
      <p className="text-gray-700 text-lg max-w-2xl mx-auto">
        Eventify is a platform that simplifies event management, resource booking, and communication for college clubs.
      </p>

      <h2 className="text-4xl font-bold text-indigo-900 mt-12 mb-8">Our College Clubs</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {clubCategories.map((category, index) => (
          <div key={index} className="bg-white shadow-xl rounded-2xl p-6 border border-gray-200 hover:shadow-2xl transition-all duration-300">
            <img 
              src={category.image} 
              alt={category.category} 
              className="w-full h-48 object-cover rounded-xl mb-4"
            />
            <h3 className="text-2xl font-semibold text-purple-700 mb-4">{category.category}</h3>

            <ul className="text-left space-y-2">
              {category.clubs.map((club, idx) => (
                <li key={idx} className="text-gray-800">
                  <a 
                    href={club.instagram} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-purple-600 hover:text-purple-800 transition"
                  >
                    {club.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}

export default About;
