import React from 'react';
import clubCategories from '../data/clubData';

function About() {
  return (
    <section className="py-20 px-10 bg-white text-center">
      <h2 className="text-4xl font-bold text-primary mb-4">About Eventify</h2>
      <p className="text-gray-600 text-lg">
        Eventify is a club management platform designed to streamline event organization, resource booking, and communication. 
        It simplifies managing college events by providing an intuitive interface for organizers and attendees alike.
      </p>

      <br />
      <h2 className="text-4xl font-bold text-primary mb-8">Our College Clubs</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        {clubCategories.map((category, index) => (
          <div 
            key={index} 
            className="bg-gray-100 shadow-lg rounded-lg p-6 hover:shadow-2xl transition-all duration-300"
          >
            <img 
              src={category.image} 
              alt={category.category} 
              className="w-full h-48 object-cover rounded-lg mb-4"
            />
            <h3 className="text-2xl font-semibold text-primary mb-4">{category.category}</h3>

            <ul className="text-left list-disc list-inside">
              {category.clubs.map((club, idx) => (
                <li key={idx} className="mb-4 text-gray-700">
                  <a 
                    href={club.instagram} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-500 hover:underline font-semibold"
                  >
                    {club.name}
                  </a>

                  {/* Club Stats */}
                  <div className="text-sm text-gray-600 mt-1">
                    🎯 <strong>Members:</strong> {club.stats.members} | 
                    🎉 <strong>Events Conducted:</strong> {club.stats.eventsConducted} | 
                    🏆 <strong>Awards Won:</strong> {club.stats.awardsWon}
                  </div>

                  {/* Event Highlights */}
                  {club.events && (
                    <div className="mt-2">
                      <h4 className="text-md font-semibold">Event Highlights:</h4>
                      {club.events.map((event, eventIdx) => (
                        <div key={eventIdx} className="mt-2 bg-white p-3 shadow-md rounded-lg">
                          <h5 className="font-bold text-gray-700">{event.title}</h5>
                          <p className="text-sm text-gray-600">{event.description}</p>
                          
                          {event.image && (
                            <img 
                              src={event.image} 
                              alt={event.title} 
                              className="mt-2 w-full h-32 object-cover rounded-lg"
                            />
                          )}
                          
                          {event.video && (
                            <video 
                              controls 
                              className="mt-2 w-full rounded-lg"
                            >
                              <source src={event.video} type="video/mp4" />
                              Your browser does not support the video tag.
                            </video>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
}

export default About;
