import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaCalendarAlt, FaBook, FaUsers, FaClock, FaMapMarkerAlt, FaBullhorn, FaArrowRight } from 'react-icons/fa';

function Home() {
  const [recentEvents, setRecentEvents] = useState([]);
  const [announcements, setAnnouncements] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecentEvents();
    fetchAnnouncements();
  }, []);

  const fetchRecentEvents = async () => {
    try {
      const response = await fetch('/api/events?limit=3');
      if (response.ok) {
        const data = await response.json();
        setRecentEvents(data);
      }
    } catch (error) {
      console.error('Error fetching recent events:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchAnnouncements = async () => {
    try {
      const response = await fetch('/api/announcements?limit=5');
      if (response.ok) {
        const data = await response.json();
        setAnnouncements(data);
      }
    } catch (error) {
      console.error('Error fetching announcements:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Hero Section */}
        <div className="text-center mb-16">
          <h1 className="text-3xl font-bold text-gray-900">Welcome to Your Dashboard</h1>
          <p className="mt-4 text-lg text-gray-500">
            Explore events, access resources, and stay connected with your college community.
          </p>
        </div>

        {/* About Eventify Section */}
        <div className="bg-white rounded-lg shadow-lg p-8 mb-12">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">About Eventify</h2>
          <div className="prose max-w-none text-gray-600">
            <p className="mb-4">
              Eventify is your comprehensive college event management and resource sharing platform designed specifically for CBIT students and faculty. Our platform streamlines the way you discover, participate in, and manage college activities.
            </p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">For Students</h3>
                <ul className="list-disc pl-5 space-y-2">
                  <li>Stay updated with latest college events and activities</li>
                  <li>Access study materials and resources</li>
                  <li>Participate in discussions and forums</li>
                  <li>Get instant notifications for important announcements</li>
                </ul>
              </div>
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">For Faculty</h3>
                <ul className="list-disc pl-5 space-y-2">
                  <li>Create and manage department events</li>
                  <li>Share academic resources and materials</li>
                  <li>Make important announcements</li>
                  <li>Track event participation and engagement</li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        {/* Announcements Section */}
        <div className="mb-12">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-900">Latest Announcements</h2>
            <FaBullhorn className="text-blue-500 text-2xl" />
          </div>
          <div className="bg-white rounded-lg shadow-lg overflow-hidden">
            {announcements.length > 0 ? (
              <div className="divide-y divide-gray-200">
                {announcements.map((announcement) => (
                  <div key={announcement.id} className="p-6 hover:bg-blue-50 transition-colors">
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-2">
                          {announcement.title}
                        </h3>
                        <p className="text-gray-600">{announcement.content}</p>
                      </div>
                      <span className="text-sm text-gray-500">
                        {new Date(announcement.date).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-6 text-center text-gray-500">
                No new announcements at the moment.
              </div>
            )}
          </div>
        </div>

        {/* Quick Access Cards */}
        <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3 mb-12">
          <Link
            to="/events"
            className="bg-white overflow-hidden shadow-lg rounded-lg hover:shadow-xl transition-shadow duration-300 ease-in-out"
          >
            <div className="p-6">
              <div className="flex items-center">
                <FaCalendarAlt className="h-8 w-8 text-blue-500" />
                <h3 className="ml-3 text-xl font-medium text-gray-900">Events</h3>
              </div>
              <p className="mt-4 text-base text-gray-500">
                View upcoming events, workshops, and activities happening in your college.
              </p>
            </div>
          </Link>

          <Link
            to="/resources"
            className="bg-white overflow-hidden shadow-lg rounded-lg hover:shadow-xl transition-shadow duration-300 ease-in-out"
          >
            <div className="p-6">
              <div className="flex items-center">
                <FaBook className="h-8 w-8 text-blue-500" />
                <h3 className="ml-3 text-xl font-medium text-gray-900">Resources</h3>
              </div>
              <p className="mt-4 text-base text-gray-500">
                Access study materials, previous year papers, and academic resources.
              </p>
            </div>
          </Link>

          <Link
            to="/about"
            className="bg-white overflow-hidden shadow-lg rounded-lg hover:shadow-xl transition-shadow duration-300 ease-in-out"
          >
            <div className="p-6">
              <div className="flex items-center">
                <FaUsers className="h-8 w-8 text-blue-500" />
                <h3 className="ml-3 text-xl font-medium text-gray-900">Community</h3>
              </div>
              <p className="mt-4 text-base text-gray-500">
                Learn about our college community, clubs, and departments.
              </p>
            </div>
          </Link>
        </div>

        {/* Recent Events Section */}
        <div className="mb-12">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-900">Recent Events</h2>
            <Link
              to="/events"
              className="text-blue-600 hover:text-blue-800 font-medium flex items-center"
            >
              View All Events
              <FaArrowRight className="ml-2" />
            </Link>
          </div>
          <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
            {loading ? (
              <div className="col-span-3 flex justify-center py-12">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
              </div>
            ) : recentEvents.length > 0 ? (
              recentEvents.map((event) => (
                <div key={event.id} className="bg-white rounded-lg shadow-md overflow-hidden">
                  <div className="p-6">
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">{event.title}</h3>
                    <p className="text-gray-600 mb-4 line-clamp-2">{event.description}</p>
                    <div className="space-y-2">
                      <div className="flex items-center text-sm text-gray-500">
                        <FaCalendarAlt className="mr-2 text-blue-500" />
                        <span>{new Date(event.date).toLocaleDateString()}</span>
                      </div>
                      <div className="flex items-center text-sm text-gray-500">
                        <FaClock className="mr-2 text-blue-500" />
                        <span>{event.time}</span>
                      </div>
                      <div className="flex items-center text-sm text-gray-500">
                        <FaMapMarkerAlt className="mr-2 text-blue-500" />
                        <span>{event.venue}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="col-span-3 text-center py-12">
                <p className="text-gray-500">No upcoming events at the moment.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
